$(function () {
    getDeptData();


})

function getDeptData(){

    $.ajax({
        url : "/hr/getFirstLvlDept",
        type : "POST",
        dataType : "json",
        data : {

        },
        success : function(data) {
            var lvl1DeptList = data;

            var UITree = function() {
                var n = function() {
                    $("#tree_3").jstree({
                        core: {
                            themes: {
                                responsive: !1
                            },
                            check_callback: !0,

                            data: [
                                {
                                text: lvl1DeptList[0].departmentName,

                                    children: [

                                        {
                                            text: "Sub Nodes",
                                            icon: "fa fa-folder icon-state-danger",

                                            children: [

                                                {
                                                    text: "Item 1",
                                                    icon: "fa fa-file icon-state-warning"
                                                },
                                                {
                                                    text: "Item 2",
                                                    icon: "fa fa-file icon-state-success"
                                                },
                                                {
                                                    text: "Item 3",
                                                    icon: "fa fa-file icon-state-default"
                                                },
                                                {
                                                    text: "Item 4",
                                                    icon: "fa fa-file icon-state-danger"
                                                },
                                                {
                                                    text: "Item 5",
                                                    icon: "fa fa-file icon-state-info"
                                                }]
                                        }]

                                    },

                                ]

                        },
                        types: {
                            "default": {
                                icon: "fa fa-folder icon-state-warning icon-lg"
                            },
                            file: {
                                icon: "fa fa-file icon-state-warning icon-lg"
                            }
                        },
                        state: {
                            key: "demo2"
                        },
                        plugins: ["contextmenu", "dnd", "state", "types"]
                    })
                };
                return {
                    init: function() {
                        n()
                    }
                }
            } ();
            App.isAngularJsApp() === !1 && jQuery(document).ready(function() {
                UITree.init()
            });

        }
    })

}



